{% if product.metafields.custom.species_latin %}
<div class="species">
<div class="species__img">
<img src="{{product.featured_image | image_url }}"></div>
<div class="species__strain">Strain: <a href="#" style="text-decoration: underline">{{ product.metafields.custom.species_latin }}</a></div>
</div>

<style>
.species {
    display: flex;
    align-items: center;
    background: #F3F3F3;
    padding: 0.25rem 1rem;
    gap: 1rem;
    justify-content: center;
    width: fit-content;
}

.species__img {
height: 30px;
width: 30px;
border-radius: 50px;
overflow: hidden;
}

.species__img img {
object-fit: cover;
    display: block;
    height: 100%;
    width: 100%;
}

@media only screen and (max-width: 767px) {
.species {

}
}
</style>
{% endif %}

{%comment%}<p><b>Note:</b> the course cannot be purchased directly through our website, but can be arranged directly by our team.</p><p><b>Express interest using the form below:</b></p>
<div class="klaviyo-form-Xu8VBS"></div>{%endcomment%}
<a id="mycelia-course-ufi-btn" href="https://mycelia-academy.org/on-site-courses/" class="btn">Learn More At Mycelia Academy</a>

{% assign lead_time = product.metafields.custom.lead_time %}
{% if lead_time != blank %}
<div class="lead-time"><div class="lead-time__icon"><svg xmlns="http://www.w3.org/2000/svg" width="800px" height="800px" viewBox="0 0 24 24" fill="none">
<path opacity="1" d="M10 7L2 7" stroke="#fff" stroke-width="1.5" stroke-linecap="round"></path>
<path opacity="1" d="M8 12H2" stroke="#fff" stroke-width="1.5" stroke-linecap="round"></path>
<path opacity="1" d="M10 17H2" stroke="#fff" stroke-width="1.5" stroke-linecap="round"></path>
<circle cx="17" cy="12" r="5" stroke="#fff" stroke-width="1.5"></circle>
<path d="M17 10V11.8462L18 13" stroke="#1C274C" stroke-width="1.5" stroke-linecap="round" stroke-linejoin="round"></path>
</svg></div><div class="lead-time__text">Lead time: {{ lead_time }}</div></div>

<style>
.lead-time {
display: flex;
align-items: center;
gap: .5rem;
background: #000;
color: #fff;
padding: 0 .75rem;
width: fit-content;
border-radius: 2px;
}

.lead-time__icon {
display: flex;
}

.lead-time__icon svg {
height: 30px;
width: 30px;
fill: #fff;
}

</style>
{% endif %}
